contracts/apps/HexlinkHelper.sol.content:
// SPDX-License-Identifier: MIT
// Hexlink Contracts

pragma solidity ^0.8.8;

import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "../hexlink/IHexlink.sol";
import "../auth/AuthProof.sol";

contract HexlinkHelper {
    using Address for address;

    event Deploy(bytes32 indexed name, address indexed impl, address account);
    IHexlink immutable hexlink;
    address immutable accountBase;
    address immutable redPacket;

    constructor(
        address _hexlink,
        address _redPacket
    ) {
        hexlink = IHexlink(_hexlink);
        accountBase = hexlink.accountBase();
        redPacket = _redPacket;
    }

    function deploy(bytes32 salt, bytes calldata txData) public returns(address) {
        address account = Clones.cloneDeterministic(accountBase, salt);
        account.functionCall(txData);
        emit Deploy(salt, accountBase, account);
        return account;
    }

    function redeployAndReset(
        bytes32 name,
        bytes calldata txData,
        AuthProof[] calldata proofs
    ) external {
        bytes32 salt = keccak256(abi.encode(name, block.timestamp));
        address account = deploy(salt, txData);
        if (proofs.length == 2) {
            IHexlink(hexlink).reset2Fac(name, account, proofs[0], proofs[1]);
        } else if (proofs.length == 1) {
            IHexlink(hexlink).reset2Stage(name, account, proofs[0]);
        } else {
            revert("HEXL014");
        }
    }

    function deployAndCreateRedPacket(
        bytes32 name,
        bytes calldata initData,
        bytes calldata txData,
        AuthProof calldata proof
    ) external payable {
        address account = hexlink.deploy(name, initData, proof);
        if (msg.value > 0) {
            Address.sendValue(payable(account), msg.value);
        }
        account.functionCall(txData);
    }
}

contracts/auth/AuthProof.sol.content:
//SPDX-License-Identifier: Unlicense

pragma solidity ^0.8.8;

/*
 * The request id is a hash over the request, contract processing
 * the request and chainId. The signature is signed over keccak256
 * hash of AuthProofToSign struct
 */
struct AuthProof{
    uint256 issuedAt;
    bytes32 identityType;
    bytes32 authType;
    bytes signature; // aggregated signature
}

contracts/hexlink/IHexlink.sol.content:
// SPDX-License-Identifier: MIT
// Hexlink Contracts

pragma solidity ^0.8.8;

import "@openzeppelin/contracts/interfaces/IERC1271.sol";
import "../auth/AuthProof.sol";

interface IHexlink {
    function accountBase() external view returns (address);

    function addressOfName(bytes32 name) external view returns (address);

    function bumpNonce(
        bytes32 name,
        AuthProof calldata proof
    ) external;

    function deploy(
        bytes32 name,
        bytes calldata txData,
        AuthProof calldata proof
    ) external returns(address);

    function reset(
        bytes32 name,
        address account,
        AuthProof calldata proof
    ) external;

    function reset2Fac(
        bytes32 name,
        address account,
        AuthProof calldata proof1,
        AuthProof calldata proof2
    ) external;

    function reset2Stage(
        bytes32 name,
        address account,
        AuthProof calldata proof
    ) external;
}
