}","keccak256":"0x5447ca4e3ee460778f6f9925078d02b2b14e48cfb6d191a68fa24510beb911de","license":"Unlicense"},"contracts/account/ModuleManager.sol":{"content":"
//SPDX-License-Identifier: Unlicense

pragma solidity ^0.8.12;

/* solhint-disable avoid-low-level-calls */

library ModuleStorage {
    struct Layout {
        mapping(bytes32 => address) modules;
    }

    bytes32 internal constant STORAGE_SLOT =
        keccak256('hexlink.storage.account.modules');

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

abstract contract ModuleManager {
    function getModule(bytes32 key) view public returns(address) {
        return ModuleStorage.layout().modules[key];
    }

    function _setModule(bytes32 key, address module) internal {
        ModuleStorage.layout().modules[key] = module;
    }
}","keccak256":"0x11196eb5388cc4dbae0073561ae8eb46d981dcb58b4958af20a6aef827ade288","license":"Unlicense"},"contracts/account/modules/IAuthModule.sol":{"content":"// SPDX-License-Identifier: MIT
