pragma solidity ^0.8.12;

import "../../interfaces/IAccountFactory.sol";

library ERC4972AccountStorage {
    bytes32 internal constant STORAGE_SLOT =
        keccak256('hexlink.account.erc4972');

    struct Layout {
        bytes32 nameType;
        bytes32 name;
    }

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

abstract contract ERC4972Account is IERC4972Account {
    IAccountFactory immutable public hexlink;

    modifier onlySelf() {
        require(msg.sender == address(this), "invalid caller");
        _;
    }

    modifier onlyOwnedAccount(bytes32 nameType, bytes32 name) {
        require(
            hexlink.getOwnedAccount(nameType, name) == address(this),
            "not owned account"
        );
        _;
    }

    constructor(address hexlink_) {
        hexlink = IAccountFactory(hexlink_);
    }

    function _setName(bytes32 nameType, bytes32 name) internal {
        ERC4972AccountStorage.layout().nameType = nameType;
        ERC4972AccountStorage.layout().name = name;
    }

    function getNameType() public view override returns(bytes32) {
        return ERC4972AccountStorage.layout().nameType;
    }

    function getName() public view override returns(bytes32) {
        return ERC4972AccountStorage.layout().name;
    }

    function getNameRegistry() external view override returns(address) {
        return address(hexlink);
    }
}","keccak256":"0xdcadd3788811ef90e672a5e1a0b5fe31ef447b7b8977427d5dd0cee0cbc0779f","license":"Unlicense"},"contracts/hexlink/Hexlink.sol":{"content":"// SPDX-License-Identifier: MIT
// Hexlink Contracts

pragma solidity ^0.8.12;

import "@solidstate/contracts/access/ownable/Ownable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";

import "../interfaces/IAccountFactory.sol";
import "../interfaces/IERC4972.sol";
import "../interfaces/IHexlinkERC1967Proxy.sol";
import "../account/Account.sol";
import "../utils/EntryPointStaker.sol";

library HexlinkStorage {
    bytes32 internal constant STORAGE_SLOT = keccak256('hexlink.main');

    struct Layout {
        address accountImplementation;
        mapping(bytes32 => address) providers;
        mapping(bytes32 => address) validators;
    }

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

contract Hexlink is
    IAccountFactory,
    Initializable,
    EntryPointStaker,
    UUPSUpgradeable
{
    event AccountDeployed(
        bytes32 indexed nameType,
        bytes32 indexed name,
        address indexed account
    );

    function initialize(address owner) public initializer {
        _transferOwnership(owner);
    }

    /** Default Auth Provider */

    function setAuthProviders(
        bytes32[] memory nameTypes,
        address[] memory providers,
        address[] memory validators
    ) external onlyOwner {
        require(
            providers.length == nameTypes.length
                && providers.length == validators.length,
            "array length mismatch"
        );
        for (uint256 i = 0; i < providers.length; i++) {
            HexlinkStorage.layout().providers[nameTypes[i]] = providers[i];
            HexlinkStorage.layout().validators[nameTypes[i]] = validators[i];
        }
    }

    function getAuthProvider(
        bytes32 nameType
    ) public view override returns(address) {
        return HexlinkStorage.layout().providers[nameType];
    }

    function getDefaultValidator(
        bytes32 nameType
    ) public view returns(address) {
        return HexlinkStorage.layout().validators[nameType];
    }

    function setAccountImplementation(address impl) external onlyOwner {
        HexlinkStorage.layout().accountImplementation = impl;
    }

    function getAccountImplementation() public view override returns(address) {
        return HexlinkStorage.layout().accountImplementation;
    }

    /** IERC4972 */

    function getOwnedAccount(
        bytes32 nameType,
        bytes32 name
    ) public view override returns(address) {
        return Clones.predictDeterministicAddress(
            address(this),
            _nameHash(nameType, name)
        );
    }

    /** IAccountFactory */

    function deploy(
        bytes32 nameType,
        bytes32 name
    ) external override returns(address account) {
        account = Clones.cloneDeterministic(
            address(this),
            _nameHash(nameType, name)
        );
        address provider = getAuthProvider(nameType);
        address validator = getDefaultValidator(nameType);
        require(provider != address(0), "unsupported name type");
        bytes memory data = abi.encodeWithSelector(
            Account.initialize.selector,
            nameType,
            name,
            provider,
            validator
        );
        address impl = getAccountImplementation();
        IHexlinkERC1967Proxy(account).initProxy(impl, data);
        emit AccountDeployed(nameType, name, account);
    }

    /** UUPSUpgradeable */

    function _authorizeUpgrade(
        address newImplementation
    ) internal view onlyOwner override { }

    /** utils */

    function _nameHash(
        bytes32 nameType,
        bytes32 name
    ) internal pure returns(bytes32) {
        return keccak256(abi.encode(nameType, name));
    }
}","keccak256":"0x824db83a878519b4cf9cf20d83b10a5911339e14abc7de713d42d1149658ca80","license":"MIT"},"contracts/interfaces/IAccountFactory.sol":{"content":"// SPDX-License-Identifier: MIT
